import React, { useState } from "react";
import { Button, TextField, Typography } from "@mui/material";

const DataUploadForm = () => {
  const [fullName, setFullName] = useState("");
  const [country, setCountry] = useState("");
  const [county, setCounty] = useState("");
  const [location, setLocation] = useState("");
  const [town, setTown] = useState("");
  const [csvFile, setCsvFile] = useState(null);
  const [predictions, setPredictions] = useState([]);

  const handleFileChange = (e) => setCsvFile(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!csvFile) {
      alert("Please upload a CSV file before submitting.");
      return;
    }

    const formData = new FormData();
    formData.append("fullName", fullName);
    formData.append("country", country);
    formData.append("county", county);
    formData.append("location", location);
    formData.append("town", town);
    formData.append("csvFile", csvFile);

    // Send CSV data to backend (e.g., using fetch or axios)
    try {
      const response = await fetch('/api/predict/', {
        method: 'POST',
        body: formData,
      });

      if (response.ok){
      const data = await response.json();
      console.log('Predictions', data.predictions);
      } else {
        console.error('Error:', )
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error submitting form.');
    }

    // Reset form after submission
    setFullName("");
    setCountry("");
    setCounty("");
    setLocation("");
    setTown("");
    setCsvFile(null);
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px", maxWidth: "400px", margin: "auto" }}>
      <Typography variant="h5" align="center">Data Upload Form</Typography>
      <TextField label="Full Name" variant="outlined" value={fullName} onChange={(e) => setFullName(e.target.value)} required />
      <TextField label="Country" variant="outlined" value={country} onChange={(e) => setCountry(e.target.value)} required />
      <TextField label="County" variant="outlined" value={county} onChange={(e) => setCounty(e.target.value)} required />
      <TextField label="Location" variant="outlined" value={location} onChange={(e) => setLocation(e.target.value)} required />
      <TextField label="Town" variant="outlined" value={town} onChange={(e) => setTown(e.target.value)} required />
      <Button variant="outlined" component="label">Upload CSV<input type="file" accept=".csv" hidden onChange={handleFileChange} /></Button>
      {csvFile && <Typography>Uploaded File: {csvFile.name}</Typography>}
      <Button type="submit" variant="contained" color="primary">Submit</Button>
    </form>
  );
};

export default DataUploadForm;
