import React, { useState } from "react";
import axios from "axios";

const CSVUpload = () => {
  const [file, setFile] = useState(null);
  const [factors, setFactors] = useState({
    healthcare: 1.1,
    employment: 1.0,
    institutions: 1.0,
  });
  const [predictions, setPredictions] = useState([]);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleFactorChange = (e) => {
    const { name, value } = e.target;
    setFactors((prev) => ({ ...prev, [name]: parseFloat(value) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file);
    formData.append("factors", JSON.stringify(factors));

    try {
      const response = await axios.post("http://localhost:8000/upload-csv/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setPredictions(response.data);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  return (
    <div>
      <h1>Upload CSV and Predict Population</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept=".csv" onChange={handleFileChange} required />
        <div>
          <label>
            Healthcare Multiplier:
            <input
              type="number"
              step="0.1"
              name="healthcare"
              value={factors.healthcare}
              onChange={handleFactorChange}
            />
          </label>
        </div>
        <div>
          <label>
            Employment Multiplier:
            <input
              type="number"
              step="0.1"
              name="employment"
              value={factors.employment}
              onChange={handleFactorChange}
            />
          </label>
        </div>
        <div>
          <label>
            Institutions Multiplier:
            <input
              type="number"
              step="0.1"
              name="institutions"
              value={factors.institutions}
              onChange={handleFactorChange}
            />
          </label>
        </div>
        <button type="submit">Upload and Predict</button>
      </form>
      <h2>Predictions</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Population</th>
            <th>Predicted Population</th>
          </tr>
        </thead>
        <tbody>
          {predictions.map((row, index) => (
            <tr key={index}>
              <td>{row.name}</td>
              <td>{row.population}</td>
              <td>{row.predicted_population}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CSVUpload;
