import React, { useState} from "react";
import axios from "axios";


axios.defaults.xsrfCookieName = "csrftoken";
axios.defaults.xsrfHeaderName = "X-CSRFToken";

const Form = () => {  
  const [file, setFile] = useState(null); 
  const [result, setResult] = useState(null);  

const handleFileChange = (e) => {  
  setFile(e.target.files[0]);
}

const handleSubmit = async() =>{
  if (!file) {
    alert("Please Upload File")

    return;
  }
  const formData = new FormData (); 
  formData.append("file", file );

  try {
      const response = await axios.post("http://127.0.0.1:8000/run-model/",formData,{
          headers: {
            "Content-Type": "multipart/form-data",
          },
      });

      setResult(response.data);
  } catch (error) { 
    console.error( error);
    alert("An error occured when running the model");
  }
};

fetch('run-model', {
  method: 'POST', 
  body: formData,
})

   .then((response) => response.json())
   .then((data) => {
    if  (data.training_plot && data.prediction_plot) {
      setTrainingPlot(`data:image/png;base64,${data.training_plot}`);
      setPredic
    }
   })
return (
  <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Run Traffic Prediction Model</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleSubmit} style={{ marginLeft: "10px", padding: "10px 20px" }}>
          Run Model
      </button>

      {result && (
          <div>
              <h2>Results</h2>
              <p>RMSE: {result.rmse}</p>

              <img 
                 src={`data:image/jpg;base64,${result.plot}`}
                 alt="Training Loss vs Validation Loss"
                 style={{ maxWidth: "100%", height: "auuto" }}
              />
              <img
                  src={`data:image/png;base64,${result.plot}`}
                  alt="Prediction vs Actual"
                  style={{ maxWidth: "100%", height: "auto" }}
              />
          </div>
      )}
  </div>
);
}

export default Form;
