import React, { useState } from "react";
import { Button, TextField, Typography } from "@mui/material";

const DataUploadForm = () => {
  const [fullName, setFullName] = useState("");
  const [country, setCountry] = useState("");
  const [county, setCounty] = useState("");
  const [location, setLocation] = useState("");
  const [town, setTown] = useState("");
  const [csvFile, setCsvFile] = useState(null);
  const [ factors, setFactoirs] = useState({

    healthcare: 1.1,
    employment: 1.0,
  institutions: 1.0,
  });
  const [predictions, setPredictions] = useState([]);


  const handleFileChange = (e) => setCsvFile(e.target.files[0]);


  const handleFactorChange = (e) => {
    const { name, value } = e.target;
    setFactors((prev) => ({ ...prev, [name]: parseFloat(value)}))
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!csvFile) {
      alert("Please upload a CSV file before submitting.");
      return;
    }

    const formData = new FormData();
    formData.append("fullName", fullName);
    formData.append("country", country);
    formData.append("county", county);
    formData.append("location", location);
    formData.append("town", town);
    formData.append("csvFile", csvFile);

    // Simulating a backend call
    console.log("Form Data Submitted:", { fullName, country, county, location, town, csvFile });

    // Reset form after submission
    setFullName("");
    setCountry("");
    setCounty("");
    setLocation("");
    setTown("");
    setCsvFile(null);
    alert("Form submitted successfully!");
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px", maxWidth: "400px", margin: "auto" }}>
      <Typography variant="h5" align="center">
        Data Upload Form
      </Typography>
      <TextField
        label="Full Name"
        variant="outlined"
        value={fullName}
        onChange={(e) => setFullName(e.target.value)}
        required
      />
      <TextField
        label="Country"
        variant="outlined"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
        required
      />
      <TextField
        label="County"
        variant="outlined"
        value={county}
        onChange={(e) => setCounty(e.target.value)}
        required
      />
      <TextField
        label="Location"
        variant="outlined"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        required
      />
      <TextField
        label="Town"
        variant="outlined"
        value={town}
        onChange={(e) => setTown(e.target.value)}
        required
      />
      <Button variant="outlined" component="label">
        Upload CSV
        <input
          type="file"
          accept=".csv"
          hidden
          onChange={handleFileChange}
        />
      </Button>
      {csvFile && <Typography>Uploaded File: {csvFile.name}</Typography>}
      <Button type="submit" variant="contained" color="primary">
        Submit
      </Button>
    </form>
  );
};

export default DataUploadForm;
