import React, { useState } from 'react';
import { Container, Typography, Button } from '@mui/material';
import { useNavigate } from "react-router-dom";
import backgroundImage from './h1_hero.jpg';
const LandingPage = () => {
    const [step, setStep] = useState("initial");
    const navigate = useNavigate();

    const handleGetStartedClick = () => {
        setStep("authOptions");

    };

    const handleLanguageSelection = (language) => {
       alert(`You Selected ${language}`);
        //add navigation fuctionality
    };
    const handleSignup = () => {    
        if (language == "English") {
            navigate("/signup-english");
        } else {
            navigate("/signup-kiswahili");
        }
    };

    const handleLogin = () => {
        if (language == "English") {
            navigate("/signup-english");
        } else {
            navigate("/signup-kiswahili")
        }
    };

    return (
        <div style={backgroundStyle}>
        <Container style={{ textAlign: 'center', marginTop: '40px', color: 'white' }}>
            <Typography variant="h3" gutterBottom>
                Welcome to Virtual Urban Planner 
            </Typography>
            <Typography variant="h6" gutterBottom>
                Harness AI to predict population growth,
                 traffic patterns and infrastructure needs.
            </Typography>
            <div style={{ textAlign: "center", marginTop: "50px"}}>

                    <button onClick={handleGetStartedClick} style={buttonStyle}>
                        Get Started
                    </button>
            { step === "authOptions" && (
                <div style={StyleSheet.content}>
                    <h1>Select an Option</h1>
                    <button onClick={handleSignup} style={StyleSheet.button}>
                        Signup
                    </button>
                    <button onClick={handleLogin}  style={StyleSheet.button}>
                        Login
                    </button>
                    </div>
            )}
           {}

                </div>
        </Container>
        </div>
    );
};

const buttonStyle = {
    padding: "10px 20px",
    margin: "10px",
    fontSize: "16px",
    cursor: "pointer",
    backgroudColor: "blue",
    borderRadius: "10px",
    border: "none",
    color: "blue",

};
const backgroundStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroudSize: 'cover',
    backgroudRepeat: 'no-repeat',
    backgroundPosition: 'center',
    height: '80vh',
    width: '97vw',
};

export default LandingPage;
