import React, { useState } from "react";
import axios from "axios";

axios.defaults.xsrfCookieName = "csrftoken";
axios.defaults.xsrfHeaderName = "X-CSRFToken";

const Form = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [trainingPlot, setTrainingPlot] = useState(null);
  const [predictionPlot, setPredictionPlot] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) {
      alert("Please upload a file!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:8000/run-model/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data) {
        setResult(response.data);
        if (response.data.training_plot && response.data.prediction_plot) {
          setTrainingPlot(`data:image/png;base64,${response.data.training_plot}`);
          setPredictionPlot(`data:image/png;base64,${response.data.prediction_plot}`);
        } else {
          console.error("Missing plot data in response");
        }
      }
    } catch (error) {
      console.error("An error occurred when running the model:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px", color: "blue" }}>
      <h1>Run Traffic Prediction Model</h1>
      <div>
        <h2 style={{}}>Upload Traffic DataSet</h2>
      <input type="file" onChange={handleFileChange} />
      </div>
      <input type="file" onChange={handleFileChange} />

      <button
        onClick={handleSubmit}
        style={{ marginLeft: "10px", padding: "10px 20px" }}
      >
        Run Model
      </button>

      {result && (
        <div style={ {display: "flex"}}>
          <h2>Results</h2>
          <p>RMSE: {result.rmse}</p>

          {trainingPlot && (
            <div>
              <h3>Training History</h3>
              <img
                src={trainingPlot}
                alt="Training Loss vs Validation Loss"
                style={{ maxWidth: "100%", height: "auto" }}
              />
            </div>
          )}

          {predictionPlot && (
            <div>
              <h3>Prediction vs Actual</h3>
              <img
                src={predictionPlot}
                alt="Prediction vs Actual"
                style={{ maxWidth: "100%", height: "auto" }}
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Form;
